sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   var user = g_form.getValue('assigned_to');

   //if non è necessario 
	g_form.setMandatory('work_notes', 'due_date', true);
	g_form.setMandatory('due_date', true);
	g_form.setValue('short_description', "Questo record è stato assegnato inizialmente a " + user);
	g_form.setValue('priority', '5');
	g_form.addInfoMessage("il campo priority è stato cambiato");


}
