function onSubmit() {

var pri = g_form.getValue('priority');

//controllo con if se il valore del campo priority corrisponde ad 1

if (pri == '1') {
// per il messaggio di conferma abbiamo bisogno di una variabile che controlli il valore del botone cliccato (ok = true, annulla = false)
};
	var input = confirm("sei sicuro di voler creare un record con priorità alta?");
		if(input == true){ //se l'utente clicca il bottone ok
			return true; //il codice va avanti
		}
		else {
			return false;
		}
}	
else 
	{
		alert("stai creando un record con priorità 1");
	}	
}
