function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

	try {
	if(newWalue != '2' && newValue != '1'){
	g_form.setMandatory('work_notes', true);

} else {
	g_form.setMandatory('work_notes', false);
}
// metto else perché così se poi ritorno su 1 e 2 mi toglie l'asterisco. 
// Se non lo metto e torno indietro non mi toglie l'asterisco.

	} // chiusura blocco try

	catch(err) { //dicitura catch
		jslog(">>>>>>>>>>>>" + err.message);
	}

}
